import { Formik } from 'formik';
import * as Yup from 'yup';
import { CircularProgress, InputLabel, TextField } from '@material-ui/core';


interface Props {
    handleSubmit: () => void;
}

function LoginForm ({ handleSubmit }: Props): JSX.Element { 
    return (
        <Formik
            initialValues={{
                username: '',
                password: ''
            }}
            validationSchema={Yup.object().shape({
                username: Yup.string().required('Username is reqeuired!').min(3, 'Username is too short'),
                password: Yup.string().required('Password is requried!').min(5, 'Password is too short!')
            })}
            onSubmit={handleSubmit}
        >
            {({ handleSubmit, handleChange, values, errors, touched }) => (
            <form onSubmit={handleSubmit} noValidate>
            <InputLabel>
              username
            </InputLabel>
            <TextField
                id='username'
                name='username'
                value={values.username}
                onChange={handleChange}
                InputProps={{
                    disableUnderline: true
                }}
                variant="outlined"

            />
            <InputLabel>
              password
            </InputLabel>
            <TextField
                id='password'
                name='password'
                value={values.password}
                onChange={handleChange}
                InputProps={{
                    disableUnderline: true
                }}

            />
            </form>
            )}    
        </Formik>
    )
}
export default LoginForm;
