import { Formik } from 'formik';
import * as Yup from 'yup';
import { CircularProgress, InputLabel, TextField } from '@material-ui/core';
import { classicNameResolver } from 'typescript';
import useStyles from './useStyles';

interface Props {
    handleSubmit: () => void;
}

function LoginForm ({ handleSubmit }: Props): JSX.Element { 
    const classes = useStyles();
    return (
        <Formik
            initialValues={{
                username: '',
                password: ''
            }}
            validationSchema={Yup.object().shape({
                username: Yup.string().required('Username is reqeuired!').min(3, 'Username is too short'),
                password: Yup.string().required('Password is requried!').min(5, 'Password is too short!')
            })}
            onSubmit={handleSubmit}
        >
            {({ handleSubmit, handleChange, values, errors, touched }) => (
            <form onSubmit={handleSubmit} className={classes.form} noValidate>
            <InputLabel className={classes.label}>
              username
            </InputLabel>
            <TextField
                id='username'
                name='username'
                error={true}
                value={values.username}
                onChange={handleChange}
                InputLabelProps={{
                    shrink: true,
                }}
                // InputProps={{
                //     disableUnderline: true
                // }}

            />
            <InputLabel className={classes.label}>
              password
            </InputLabel>
            <TextField
                id='password'
                name='password'
                value={values.password}
                onChange={handleChange}
                InputLabelProps={{
                    shrink: true,
                }}
                // InputProps={{
                //     disableUnderline: true
                // }}
            />
            </form>
            )}    
        </Formik>
    )
}
export default LoginForm;
